import React from 'react';
import './App.css';
import SearchBar from '../SearchBar/SearchBar';
import SearchResults from '../SearchResults/SearchResults';
import Playlist from '../Playlist/Playlist';

class App extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      SearchResults: [{name: 'name1', artist:'artist1', album: 'album1', id:1}
    , {name: 'name2', artist:'artist2', album: 'album2', id:2}
    , {name: 'name3', artist:'artist3', album: 'album3', id:3}]
    ,   playlistName: 'My Playlist'
    ,   playlistTracks: [{name: 'playlistname1', artist:'playstlistartist1', album: 'playlistalbum1', id:4}
        , {name: 'playlistname2', artist:'playstlistartist2', album: 'playlistalbum2', id:5}
        , {name: 'playlistname3', artist:'playstlistartist3', album: 'playlistalbum3', id:6}]
    };
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
    this.updatePlaylistName = this.updatePlaylistName.bind(this);
    this.savePlaylist = this.savePlaylist.bind(this);
  }

  addTrack(track) {
    let tracks = this.state.playlistTracks;
    if (tracks.find(savedTrack => savedTrack.id === track.id)) {
      return;
    } 

    tracks.push(track);
    this.setState({playlistTracks: tracks})
  }

  removeTrack(track) {
    let tracks = this.state.playlistTracks;
    tracks = tracks.filter(currentTrack => currentTrack.id !== track.id)
    this.setState({playlistTracks : tracks});
  }

  updatePlaylistName(name) {
    this.setState({playlistName: name});
  }

  savePlaylist() {
    alert("this method is linked to the button well")
    const trackURIs = this.state.playlistTracks.map(track => track.uri)
  }

  render() {
    return (
      <div>
        <h1>Diamond <span className="highlight">City </span> Radio</h1>
        <div className="App">
          <SearchBar /> 
          <div className= "App-playlist">
            <SearchResults SearchResults={this.state.SearchResults} 
                            onAdd={this.addTrack} /> 
            <Playlist playlistName={this.state.playlistName}
                      playlistTracks={this.state.playlistTracks}
                      onRemove={this.removeTrack}
                      onNameChange={this.updatePlaylistName}
                      onSave={this.savePlaylist} /> 
          </div>
        </div>
      </div>
    )
  }
}

